import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { TabsModule } from 'ngx-bootstrap/tabs';
import { Member } from 'src/app/_models/member';
import { MembersService } from 'src/app/_services/members.service';
import { GALLERY_CONFIG, GalleryConfig } from 'ng-gallery';
@Component({
  selector: 'app-member-detail',
  standalone:true,
  templateUrl: './member-detail.component.html',
  styleUrls: ['./member-detail.component.css'],
  imports:[CommonModule,TabsModule]
})
export class MemberDetailComponent implements OnInit  {
  member: Member| undefined
  constructor(private _member:MembersService,private _route:ActivatedRoute) {


  }
  ngOnInit(): void {
    this.loadmember();
  } 
  loadmember(){
    var username=this._route.snapshot.paramMap.get('username');
    if(!username)return;
    this._member.getMember(username).subscribe({
      next:response=>{
        this.member=response;
      }
    });
  }


}

<div class="row" *ngIf="member">
  <div class="col-4">
    <div class="card" style="width: 18rem;">
      <img class="card-img-top img-thumbnail" src="{{member.photoUrl || './assest/staticimg/download.png'}}" alt="{{member.knownAs}}">
      <div class="card-body">
        <h5 class="card-title">{{member.username}}</h5>
        <div>
          <strong>Location : </strong>
          <p>{{member.city}},{{member.country}}</p>
      </div>
      <div>
          <strong>Age : </strong>
          <p>{{member.age}}</p>
      </div>
      <div>
          <strong>Last Active : </strong>
          <p>{{member.lastActive}}</p>
      </div>
        <div class="btn btn-group d-flex">
          <button class="btn btn-primary">Like</button>
          <button class="btn btn-success">Message</button>
      </div>
      </div>
    </div>
  </div>

    <div class="col-8">
      <tabset class="member-tabs">
          <tab heading="About {{member.knownAs}}">
            <h4>Description </h4>
            <p>{{member.myProperty}} Interst Records here</p>
            <h4>LoockingFor </h4>
            <p>{{member.lookingFor}}</p>
          </tab>
          <tab heading="Interests">
            <h4>Interests </h4>
            <p>{{member.interests}}</p>
          </tab>
          <tab heading="Photes ">
            <p>Photos will go here</p>
          </tab>
          <tab heading="Message ">
            <p>Message will go here</p>
          </tab>
      </tabset>
    </div>
</div>
